mapper.update:

   UPDATE pikiuser
      SET user_following_count = user_following_count+1
    WHERE user_no = #{user_no };


   UPDATE pikiuser
    SET user_follower_count = user_follower_count+1
   WHERE user_no = #{user_no_friend};


   UPDATE pikiuser
      SET user_following_count = user_following_count-1
    WHERE user_no = #{user_no };


   UPDATE pikiuser
    SET user_follower_count = user_follower_count-1
   WHERE user_no = #{user_no_friend};
